export const getMonths = (currentMonth, currentYear) => {
    const months = [];
    for (let i = 0; i < 6; i++) {
        const date = new Date(currentYear, currentMonth + i);
        months.push({
            name: date.toLocaleString('en', { month: 'long' }),
            year: date.getFullYear(),
            month: date.getMonth()
        });
    }
    return months;
};
