import { daysOfWeek } from "../../constants";
import "./calendar.css";

export const Month = ({ month }) => {
  const { name, year } = month;

  const getDaysInMonth = (year, month) => {
    return new Date(year, month + 1, 0).getDate();
  };

  const getFirstDayOfMonth = (year, month) => {
    return new Date(year, month, 1).getDay();
  };

  const getDaysInPreviousMonth = (year, month) => {
    return new Date(year, month, 0).getDate();
  };

  const daysInMonth = getDaysInMonth(year, month.month);
  const firstDay = getFirstDayOfMonth(year, month.month);
  const days = [];
  const daysInPrevMonth = getDaysInPreviousMonth(year, month.month);

  for (let i = firstDay - 1; i >= 0; i--) {
    days.push({
      number: daysInPrevMonth - i,
      isCurrentMonth: false,
    });
  }

  for (let day = 1; day <= daysInMonth; day++) {
    days.push({
      number: day,
      isCurrentMonth: true,
    });
  }

  return (
    <div className="month">
      <h3>{name}</h3>
      <div className="days_of_week">
        {daysOfWeek.map((day, index) => (
          <div className="day_week" key={index}>
            {day}
          </div>
        ))}
      </div>
      <div className="days">
        {Array.from({ length: firstDay }).map((_, index) => (
          <div className="empty" key={`empty-${index}`} />
        ))}
        {days.map((day, index) => <div className="day" key={index}>{day}</div>)}
      </div>
    </div>
  );
};
