import React from "react";
import "./calendar.css";

export const EventPopup = ({ events, onClose }) => {
  return (
    <div className="event_popup" onClick={onClose}>
      <div className="event_popup_content" onClick={(e) => e.stopPropagation()}>
        <h3 className="event_popup_title">Events</h3>
        <div className="events_list">
          {events.length > 0 ? (
            events.map((event, index) => (
              <div className="event_item" key={index}>
                <h4>{event.title}</h4>
                <p>{event.description}</p>
                <p>{event.location}</p>
                <p>{new Date(event.date).toLocaleTimeString()}</p>
                <p>{event.type}</p>
              </div>
            ))
          ) : (
            <p>No events for this day.</p>
          )}
        </div>
        <div className="button_wrapper">
          <button className="add_event">Add event</button>
        </div>
      </div>
    </div>
  );
};
